import React, { useState, useEffect } from 'react';

const API_URL = 'http://localhost:8000/api/products';

const AdminProducts = () => {
  const [products, setProducts] = useState([]);
  const [editId, setEditId] = useState(null);
  const [editStock, setEditStock] = useState('');
  const [newName, setNewName] = useState('');
  const [newStock, setNewStock] = useState('');
  const [addError, setAddError] = useState('');

  useEffect(() => {
    fetch(API_URL)
      .then((res) => res.json())
      .then((data) => setProducts(data));
  }, []);

  const handleDelete = (id) => {
    fetch(`${API_URL}/${id}`, { method: 'DELETE' }).then((res) => {
      if (res.ok) setProducts(products.filter((p) => p.id !== id));
    });
  };

  const handleEdit = (id, stock) => {
    setEditId(id);
    setEditStock(stock);
  };

  const handleSave = (id) => {
    fetch(`${API_URL}/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ stock: editStock }),
    }).then((res) => {
      if (res.ok) {
        setProducts(
          products.map((p) => (p.id === id ? { ...p, stock: editStock } : p))
        );
        setEditId(null);
        setEditStock('');
      }
    });
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    if (!newName || !newStock) {
      setAddError('Please enter both name and stock.');
      return;
    }
    setAddError('');
    const res = await fetch(API_URL, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: newName, stock: Number(newStock) }),
    });
    if (res.ok) {
      const added = await res.json();
      setProducts([...products, added]);
      setNewName('');
      setNewStock('');
    } else {
      setAddError('Failed to add product.');
    }
  };

  return (
    <div className="p-8 max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold mb-6 text-center">Manage Products</h2>
      <form
        onSubmit={handleAddProduct}
        className="mb-6 flex gap-4 items-end"
      >
        <div>
          <label className="block text-sm font-medium mb-1">Name</label>
          <input
            type="text"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
            className="border px-2 py-1 rounded"
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Stock</label>
          <input
            type="number"
            value={newStock}
            onChange={(e) => setNewStock(e.target.value)}
            className="border px-2 py-1 rounded w-24"
          />
        </div>
        <button
          type="submit"
          className="bg-green-500 text-white px-4 py-2 rounded"
        >
          Add Product
        </button>
      </form>
      {addError && (
        <div className="text-red-500 mb-4">{addError}</div>
      )}
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2">Name</th>
            <th className="p-2">Stock</th>
            <th className="p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id} className="border-t">
              <td className="p-2">{product.name}</td>
              <td className="p-2">
                {editId === product.id ? (
                  <input
                    type="number"
                    value={editStock}
                    onChange={(e) => setEditStock(e.target.value)}
                    className="border px-2 py-1 rounded w-20"
                  />
                ) : (
                  product.stock
                )}
              </td>
              <td className="p-2 space-x-2">
                {editId === product.id ? (
                  <button
                    onClick={() => handleSave(product.id)}
                    className="bg-green-500 text-white px-2 py-1 rounded"
                  >
                    Save
                  </button>
                ) : (
                  <button
                    onClick={() => handleEdit(product.id, product.stock)}
                    className="bg-blue-500 text-white px-2 py-1 rounded"
                  >
                    Edit
                  </button>
                )}
                <button
                  onClick={() => handleDelete(product.id)}
                  className="bg-red-500 text-white px-2 py-1 rounded"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {products.length === 0 && (
        <div className="text-center text-gray-500 mt-6">No products found.</div>
      )}
    </div>
  );
};

export default AdminProducts;
