import React, { useState, useEffect } from 'react';
    import Header from '../components/Header.js';
    import PlusButton from '../components/PlusButton.js';
    import ProductCard from '../components/ProductCard.js';
    import ProductForm from '../components/ProductForm.js';
    import Login from '../components/Login.js';
    
    const API_URL = 'http://localhost:8000/api/products';
    
    function HomePage() {
      const [cards, setCards] = useState([]);
      const [showForm, setShowForm] = useState(false);
      const [error, setError] = useState("");
    
      useEffect(() => {
        fetch(API_URL)
          .then((res) => res.json())
          .then((data) => setCards(data))
          .catch(() => setError("Failed to fetch products."));
      }, []);
    
      const addCard = async (product) => {
        setError("");
        try {
          const res = await fetch(API_URL, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ name: product.name, stock: Number(product.stock) })
          });
          if (res.ok) {
            const added = await res.json();
            setCards([...cards, added]);
            setShowForm(false);
          } else {
            setError("Failed to add product.");
          }
        } catch {
          setError("Failed to add product.");
        }
      };
    
      const toggleForm = () => {
        setShowForm(!showForm);
      };
    
      return (
        <div className="flex flex-col items-center justify-between min-h-screen p-6 bg-gray-100">
          <Header />
          <div className="flex-grow flex flex-col items-center justify-center">
            <h1 className="text-3xl font-bold mb-6">Handmade Marketplace</h1>
            <PlusButton onClick={toggleForm} />
            {showForm && (
              <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
                <ProductForm onSubmit={addCard} onCancel={toggleForm} />
              </div>
            )}
            {error && <div className="text-red-500 mb-4">{error}</div>}
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 mt-6">
              {cards.map((card) => (
                <ProductCard
                  key={card.id}
                  image={card.image}
                  name={card.name}
                  productId={card.productId}
                  stock={card.stock}
                />
              ))}
            </div>
            <button
              className="mt-8 bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 transition-colors"
              onClick={() => window.location.href = '/admin/products'}
            >
              Update Products
            </button>
          </div>
        </div>
      );
    }
    
    export default HomePage;
